    private void TableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_TableMouseClicked
        DefaultTableModel model=(DefaultTableModel) Table.getModel(); 
       int n=Table.getSelectedRow();
       FirstName.setText(model.getValueAt(n, 0).toString());
       LastName.setText(model.getValueAt(n, 1).toString());
       Passport.setText(model.getValueAt(n, 2).toString());
    UserName.setText(model.getValueAt(n, 3).toString());
       Number.setText(model.getValueAt(n, 4).toString());
       Password.setText(model.getValueAt(n, 5).toString());
       cPassword.setText(model.getValueAt(n, 6).toString());
    }//GEN-LAST:event_TableMouseClicked

