private void getdata(){
    try{
        getmyconnection();
        firstname=FirstName.getText();
            lastname=LastName.getText();
            passport=Passport.getText();
            username=UserName.getText();
            number=Number.getText();
            password=String.valueOf(Password.getPassword());
            cpassword=String.valueOf(cPassword.getPassword());
            pst=cn.prepareStatement("insert into users2 values(?,?,?,?,?,?,?) ");
            pst.setString(1,firstname);
            pst.setString(2,lastname);
            pst.setString(3,passport);
            pst.setString(4,username);
            pst.setString(5,number);
            pst.setString(6,password);
            pst.setString(7, cpassword);
            
            pst.execute();
            DefaultTableModel model=(DefaultTableModel) Table.getModel();
            String mydata[]={firstname,lastname,passport,username,number,password,cpassword};
            model.addRow(mydata);
            JOptionPane.showMessageDialog(null, "Login In");
            //clear screen
            FirstName.setText(null);
            LastName.setText(null);
            Passport.setText(null);
            Number.setText(null);
            UserName.setText(null);
            Password.setText(null);
            cPassword.setText(null);
            if(rs.next()){
               MainHotel mainpage=new MainHotel();
               mainpage.setVisible(true);
               mainpage.pack();
            }
            else{  
              JOptionPane.showMessageDialog(rootPane, "fill in all fields");
            }           
        }
         catch(Exception e)
        {
           e.printStackTrace();
        }
    }
